import '../css/Dashboard.css'
import React, { Component } from 'react';

var socketReady = false;

const socket = new WebSocket("ws://192.168.50.252:13275");

socket.onopen = function () {
  socketReady = true;
  requestChart('ETH',Date().now,'1 H',1000);
  requestBalances();
}

socket.addEventListener("message", (event) => {

  let type = event.data.split('/')[0];
  let data = event.data.split('/')[1];

  if (type == 'balances'){
    let balances = JSON.parse(data);
    let totalValue = 0;
    document.getElementById("asset_wrap").innerHTML = "";
    for(let i = 0; i < balances.length; i++) {
      let price = balances[i].price != null ? round(balances[i].price) : 0;
      let amount = balances[i].amount != null ? round(balances[i].amount) : 0;
      let value = round(price*amount);
      document.getElementById("asset_wrap").innerHTML += "<div class='asset'><span class='name'>"+balances[i].ticker+"</span> <span class='h1'>1h%</span><span class='h24'>24h%</span><span class='d7'>7d%</span> <span class='price'>"+price+"</span><span class='holdings'>"+amount+"</span> <span class='value'>"+value+"</span></div>"
      totalValue+=parseFloat(value);
    }
    document.getElementById("total_value").innerHTML=round(totalValue)+"$";
  }
  console.log("Message from server ", event.data.split('/')[0]);
  console.log("Message from server ", event.data.split('/')[1]);
  console.log("Message from server ", event.data.split('/')[2]);
});

function round(x){
  return parseFloat(Number.parseFloat(x).toFixed(5));
}

function requestChart(token,since,timeframe,duration){
  request('chart/'+token+'/'+since+'/'+timeframe+'/'+1000);
}

function request(message){
  if(socketReady){
    socket.send(message);
  }else{
