import '../css/Dashboard.css'
import React, { Component } from 'react';

var socketReady = false;
var sorted = '';
var sortedTime = new Date("2019-01-01");
var sortCooldown = 200; // ms
var tokens = [];
var waiting = false;

const socket = new WebSocket("ws://192.168.50.252:13206");

socket.onopen = function () {
  socketReady = true;

  requestBalances();
}

socket.addEventListener("message", async (event) => {
//  constructor(name, price, holdings, value, domElement, h1, h24, d7, chart)
  let type = event.data.split('/')[0];
  let data = event.data.split('/')[1];
  if (type == 'balances'){
    let balances = JSON.parse(data);
    let totalValue = 0;
    document.getElementById("asset_wrap").innerHTML = "";
    for(let i = 0; i < balances.length; i++) {
      let price = balances[i].price != null ? round(balances[i].price) : 0;
      let amount = balances[i].amount != null ? round(balances[i].amount) : 0;
      let value = round(price*amount);
      let name = balances[i].ticker;
      let token = new Token(name,price,amount,value);
      tokens.push(token);


      requestChart(name,0,'1 H',168);



      totalValue+=parseFloat(value);
    }
    document.getElementById("total_value").innerHTML=round(totalValue)+"$";
  }else if(type == 'chart7D'){
    let token = getTokenByName(event.data.split('/')[1]);
    token.chart = JSON.parse(event.data.split('/')[2]);
    console.log(token.chart.open[1]);
    document.getElementById("asset_wrap").innerHTML += "<div class='asset'><span class='name'>"+token.ticker+"</span> <span class='h1'>"+((token.price/token.chart.open[1])-1)*100+"</span><span class='h24'>24h%</span><span class='d7'>7d%</span> <span class='price'>"+token.price+"</span><span class='holdings'>"+token.amount+"</span> <span class='value'>"+token.value+"</span></div>";

  }

});



function getTokenByName(name){
  for (var i=0;i<tokens.length;i++){
    if(tokens[i].name == name){
      return tokens[i];
    }
  }
}

function round(x){
  return parseFloat(Number.parseFloat(x).toFixed(5));
}

function requestChart(token,has,timeframe,limit){
  request('chart/'+token+'/'+has+'/'+timeframe+'/'+limit);
}

function request(message){
  if(socketReady){
    socket.send(message);
  }else{
    console.log("Socket is not ready yet");
  }
}

function requestBalances() {
  request('getAllBalances/');
}

function sort(how){

  if((new Date().getTime() - sortedTime.getTime()) > sortCooldown)
  {
    console.log((new Date().getTime() - sortedTime.getTime()));
    sortedTime = new Date();

    let assets = document.getElementsByClassName('asset');
    let assetWrap = document.querySelector("#asset_wrap");
    console.log(assets[0].getElementsByClassName('name')[0]);
    let swapped;
    let order = false;

    const reverse = () => {
      for (let i = 0; i <= assets.length-1; i++) {
          assetWrap.appendChild(assets[assets.length-1-i]);
      }
    }

    if(how == 'name'){
      if(sorted != 'name'){
        sorted = 'name';
        for (let i = 0; i < assets.length; i++) {
          swapped = false;
          for (let ii = 0; ii < assets.length-1; ii++) {
            //compare 1st character
            if(assets[ii].getElementsByClassName('name')[0].innerHTML.charCodeAt(0) > assets[ii+1].getElementsByClassName('name')[0].innerHTML[0].charCodeAt(0)){
              assets[ii+1].after(assets[ii]);
            }
          }
        }
      }else{
        reverse.call();
      }
    }else if(how == 'h1'){

    }else if(how == 'h24'){

    }else if(how == 'd7'){

    }else if(how == 'price'){
      if(sorted != 'price'){
        sorted = 'price';
        for (let i = 0; i < assets.length; i++) {
          swapped = false;
          for (let ii = 0; ii < assets.length-1; ii++) {
            //compare 1st character
            if(parseFloat(assets[ii].getElementsByClassName('price')[0].innerHTML) < parseFloat(assets[ii+1].getElementsByClassName('price')[0].innerHTML)){
              assets[ii+1].after(assets[ii]);
            }
          }
        }
      }else{
        reverse.call();
      }
    }else if(how == 'holdings'){
      if(sorted != 'holdings'){
        sorted = 'holdings';
        for (let i = 0; i < assets.length; i++) {
          swapped = false;
          for (let ii = 0; ii < assets.length-1; ii++) {
            //compare 1st character
            if(parseFloat(assets[ii].getElementsByClassName('holdings')[0].innerHTML) < parseFloat(assets[ii+1].getElementsByClassName('holdings')[0].innerHTML)){
              assets[ii+1].after(assets[ii]);
            }
          }
        }
      }else{
        reverse.call();
      }
    }else if(how == 'value'){
      if(sorted != 'value'){
        sorted = 'value';
        for (let i = 0; i < assets.length; i++) {
          swapped = false;
          for (let ii = 0; ii < assets.length-1; ii++) {
            if(parseFloat(assets[ii].getElementsByClassName('value')[0].innerHTML) < parseFloat(assets[ii+1].getElementsByClassName('value')[0].innerHTML)){
              assets[ii+1].after(assets[ii]);
            }
          }
        }
      }else{
        reverse.call();
      }
    }
  }
}

class Token{
  constructor(name, price, holdings, value, domElement, h1, h24, d7, chart){
    this.name = name;
    this.h1 = h1;
    this.h24 = h24;
    this.d7 = d7;
    this.price = price;
    this.holdings = holdings;
    this.value = value;
    this.chart = chart;
    this.domElement = domElement;
  }
}

class Dashboard extends React.Component{

  constructor(props){
    super(props);
  }

  render(){

    return (
      <>
      <div id="content_wrap">
        <div id='accounts_wrap'>testtt</div>
        <div id="assets_wrap">
          <div id='total_value'>1000$</div>
          <div class='label'><span>Assets</span></div>
          <div id='asset_table_rows'>
            <span class='name' onClick={() => sort('name')}>Name</span> <span class='h1' onClick={() => sort('1h')}>1h%</span><span class='h24' onClick={() => sort('24h')}>24h%</span><span class='d7' onClick={() => sort('d7')}>7d%</span> <span class='price' onClick={() => sort('price')}>Price</span> <span class='holdings' onClick={() => sort('holdings')}>Holdings</span> <span class='value' onClick={() => sort('value')}>Value</span>
          </div>
          <div id='asset_wrap'>

          </div>
        </div>
      </div>
      </>
    );

  }
};
export default Dashboard;
