class Chain{
  constructor(rpc, tokenName){
    this.rpc = rpc;
    this.tokenName = tokenName;
  }
}


class Wallets extends React.Component{



  constructor(props){
    super(props);
    this.chains = new Map();
    this.chains.set('ETH', new Chain(ethRPC, 'ETH'));
    this.chains.set('OPT', new Chain(optRPC, 'OPT'));
    this.chains.set('ARB', new Chain(arbRPC, 'ARB'));
    this.chains.set('STRK', new Chain(starkRPC, 'STRK'));
    this.chains.set('BSC', new Chain(bscRPC, 'BNB'));
    this.getBalanceGasToken.bind(this);
  }



