const bscRPC = new Web3(new Web3.providers.HttpProvider("https://bnb-mainnet.g.alchemy.com/v2/Wflh86IAxtX-p7SFc--_YxhsIINWRfPR"));

class Chain{
  constructor(rpc, tokenName){
    this.rpc = rpc;
    this.tokenName = tokenName;
  }
}


class Wallets extends React.Component{



  constructor(props){
    super(props);
    this.chains = [new Chain(ethRPC,"ETH")];
    this.getBalanceGasToken.bind(this);
  }



  getBalanceGasToken(address, chain){
    bscRPC.eth.getBalance(address).then((balance) => console.log(bscRPC.utils.fromWei(balance, "ether") + " BNB"));
  }


