import { BlurFilter, TextStyle, Application, Geometry, Mesh, Shader } from 'pixi.js';
import { Stage, Container, Sprite, Text } from '@pixi/react';
import React from 'react';
import ReactDOM from 'react-dom/client';
import './css/index.css';
import TodoApp from './components/todo.jsx';
import reportWebVitals from './reportWebVitals';
import Sketch from "react-p5";
import * as THREE from 'three'
import Stats from 'three/examples/jsm/libs/stats.module'
import vertex from './shaders/mosaic/mosaic.vert';
import fragment from './shaders/mosaic/mosaic.frag';
const root = ReactDOM.createRoot(document.getElementById('root'));

(async () =>
{
    // Create a new application
    const app = new Application();

    // Initialize the application
    await app.init({
        resizeTo: window,
        preference: 'webgl',
    });

    // Append the application canvas to the document body
    document.body.appendChild(app.canvas);

    const quadGeometry = new Geometry({
        attributes: {
            aPosition: [
                -100,
                -100, // x, y
                100,
                -100, // x, y
                100,
                100, // x, y,
                -100,
                100, // x, y,
            ],
            aUV: [0, 0, 1, 0, 1, 1, 0, 1],
        },
        indexBuffer: [0, 1, 2, 0, 2, 3],
    });

    const shader = Shader.from({
        gl: {
            vertex,
            fragment,
        },
        resources: {
            shaderToyUniforms: {
                iResolution: { value: [1920, 1080, 1], type: 'vec3<f32>' },
                iTime: { value: 0, type: 'f32' },
            },
        },
    });

    const quad = new Mesh({
        geometry: quadGeometry,
        shader,
    });

    quad.width = app.screen.width;
    quad.height = app.screen.height;
    quad.x = app.screen.width / 2;
    quad.y = app.screen.height / 2;

    app.stage.addChild(quad);

    app.ticker.add(() =>
    {
        shader.resources.shaderToyUniforms.uniforms.iTime += app.ticker.elapsedMS / 1000;
    });
})();


